import React, { useEffect, useState, useRef } from "react";
import styles from "./Navbar.module.css";
import ListProduct from "../ListProduct/ListProduct.js";
import { OverlayTrigger, Tooltip, Button, Toast } from "react-bootstrap";
export default function Navbar(props) {
	const [show, setShow] = useState(false);
	const handleClose = () => setShow(false);
	const handleShow = () => setShow(true);
	const [toastShow, setToastShow] = useState(false);
	const handleToastShow = () => setToastShow(false);
	return (
		<div className={styles.container}>
			<Toast show={toastShow} onClose={handleToastClose}>
				<Alert variant="success" onClose={() => setShow(false)} dismissible>
					<h3>Product Added Successfully</h3>
					<p>It might take few minutes to reflect in our app</p>
				</Alert>
			</Toast>
			<div>Ecommerce</div>
			<div className={styles.rightContainer}>
				<button
					type="button"
					data-bs-toggle="modal"
					data-bs-target="#exampleModal"
					className={styles.listBtn}
					onClick={handleShow}
				>
					List Product
				</button>
				<OverlayTrigger
					placement="bottom"
					overlay={<Tooltip id={`tooltip-bottom`}>{props.account}</Tooltip>}
				>
					<Button variant="outline-primary">
						{props.account?.slice(0, 5) + "..." + props.account?.slice(38, 42)}
					</Button>
				</OverlayTrigger>
			</div>
			<ListProduct
				show={show}
				handleClose={handleClose}
				loading={props.loading}
				account={props.account}
				ecommerce={props.ecommerce}
				setLoading={props.setLoading}
				productCount={props.productCount}
			/>
		</div>
	);
}
