import React, { useState } from "react";
import styles from "./ProductCard.module.css";
import { ethers } from "ethers";
function timeout(delay) {
	return new Promise((res) => setTimeout(res, delay));
}
function ProductCard(props) {
	const [loading, setLoading] = useState(false);

	const handlePurchaseProduct = async () => {
		setLoading(true);
		await props.ecommerce.functions.purchaseProduct(props.product.id, {
			value: props.product.price,
		});
		await timeout(3000);
		setLoading(false);
		window.location.reload(false);
	};
	return (
		<div
			className={`col-lg-6 col-md-12 col-sm-12 col-xs-12 ${styles.container}`}
		>
			<div className={styles.innerContainer}>
				<div className={styles.imgPlaceholder}></div>
				<div className={styles.rightContainer}>
					<div className={styles.name}>{props.product?.name}</div>
					<div className={styles.price}>
						Price : {ethers.utils.formatEther(props.product.price.toString())}{" "}
						ETH
					</div>
					<div className={styles.owner}>Owner : {props.product.owner}</div>
					{props.product.purchased ? (
						<button type="button" class="btn btn-primary disabled">
							Purchased
						</button>
					) : (
						<button
							type="button"
							class="btn btn-primary"
							onClick={handlePurchaseProduct}
						>
							{loading ? "Loading...." : "Buy"}
						</button>
					)}
				</div>
			</div>
		</div>
	);
}

export default ProductCard;
