import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import Ecommerce from "../abis/Ecommerce.json";
import Navbar from "./Navbar/Navbar";
import Products from "./Products/Products";
import "./App.css";
function App() {
	const [account, setAccount] = useState();
	const [productCount, setProductCount] = useState(0);
	const [products, setProducts] = useState([]);
	const [loading, setLoading] = useState(true);
	const [ecommerce, setEcommerce] = useState();
	const [balance, setBalance] = useState();
	const [refresh, setRefresh] = useState(false);
	const loadBlockChainData = async () => {
		if (window.ethereum) {
			const accounts = await window.ethereum.request({
				method: "eth_requestAccounts",
			});
			setAccount(accounts[0]);
			const networkId = await window.ethereum.request({
				method: "net_version",
			});
			let balance = await window.ethereum.request({
				method: "eth_getBalance",
				params: [accounts[0], "latest"],
			});
			balance = ethers.utils.formatEther(balance);
			setBalance(balance);

			const provider = new ethers.providers.Web3Provider(window.ethereum);
			const signer = provider.getSigner();
			const networkData = Ecommerce.networks[networkId];
			if (networkData) {
				await loadContract(signer, networkData);
			} else {
				window.alert("Ecommerce contract not deployed to detected network");
			}
		} else {
			window.alert(
				"Non-Ethereum browser detected. You should consider trying MetaMask!"
			);
		}
	};

	const loadContract = async (signer, networkData) => {
		setLoading(true);
		const contract = new ethers.Contract(
			networkData.address,
			Ecommerce.abi,
			signer
		);
		let temp = await contract.functions.productCount();
		temp = temp.toString();
		setProductCount(temp);
		setEcommerce(contract);
		setLoading(false);
	};

	const loadProducts = async () => {
		console.log(productCount);
		for (var i = 1; i <= productCount; i++) {
			const product = await ecommerce?.functions.products(i);
			setProducts((products) => [...products, product]);
		}
	};

	useEffect(() => {
		loadBlockChainData();
	}, []);

	useEffect(() => {
		loadProducts();
	}, [productCount, account]);

	// console.log(products);

	return (
		<div>
			<Navbar
				loading={loading}
				account={account}
				ecommerce={ecommerce}
				setLoading={setLoading}
				productCount={productCount}
			/>
			{loading && <div>Loading....</div>}
			<div>Balance : {balance}</div>
			<div>Product Count : {productCount}</div>
			<Products
				loading={loading}
				products={products}
				account={account}
				ecommerce={ecommerce}
				setLoading={setLoading}
			/>
		</div>
	);
}

export default App;
