import React, { useState } from "react";
import { ethers } from "ethers";
import { create } from "ipfs-http-client";

const client = create("https://ipfs.infura.io:5001/api/v0");

function MyVerticallyCenteredModal(props) {
	return (
		<Modal
			{...props}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
		>
			<Modal.Header closeButton>
				<Modal.Title id="contained-modal-title-vcenter">
					Modal heading
				</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<h4>Centered Modal</h4>
				<p>
					Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
					dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
					consectetur ac, vestibulum at eros.
				</p>
			</Modal.Body>
			<Modal.Footer>
				<Button onClick={props.onHide}>Close</Button>
			</Modal.Footer>
		</Modal>
	);
}

function ListProduct(props) {
	const [name, setName] = useState();
	const [price, setPrice] = useState();
	const [loading, setLoading] = useState(false);
	const [file, setFile] = useState();
	const handleNameChange = (e) => {
		setName(e.target.value);
	};
	const handlePriceChange = (e) => {
		setPrice(e.target.value);
	};

	const handleAddProduct = async () => {
		setLoading(true);
		await props.ecommerce?.functions.createProduct(
			name,
			ethers.utils.parseEther(price),
			file
		);
		setName("");
		setPrice("");
		setFile(null);
		console.log(props.productCount);
		setLoading(false);
	};

	const handleFileChange = async (e) => {
		// console.log(e.target.files);
		// const file = e.target.files[0];
		const file = e.target.files[0];
		const result = await client.add(file);
		setFile(result);
		// console.log(files);
		// for (var file in files) {
		// 	const result = await client.add(files[file]);
		// 	console.log(result);
		// 	setFiles((prev) => [...prev, result.path]);
		// }
	};

	const resetInputs = () => {
		setName("");
		setPrice("");
		setLoading(false);
		setFile(null);
	};

	return (
		<div
			className="modal fade"
			id="exampleModal"
			data-bs-backdrop="static"
			data-bs-keyboard="false"
			tabindex="-1"
			aria-labelledby="exampleModalLabel"
			aria-hidden="true"
		>
			<div className="modal-dialog modal-dialog-centered">
				<div className="modal-content">
					<div className="modal-header">
						<h5 class="modal-title" id="exampleModalLabel">
							Add Product
						</h5>
						<button
							type="button"
							class="btn-close"
							data-bs-dismiss="modal"
							aria-label="Close"
							onClick={resetInputs}
						></button>
					</div>
					<div className="modal-body">
						<div class="mb-3">
							<label for="exampleFormControlInput1" class="form-label">
								Product Name
							</label>
							<input
								class="form-control"
								id="exampleFormControlInput1"
								placeholder="Enter Product Name"
								value={name}
								onChange={handleNameChange}
							></input>
						</div>
						{client ? (
							<div class="mb-3">
								<label for="exampleFormControlInput2" class="form-label">
									Product Price
								</label>
								<input
									class="form-control"
									id="exampleFormControlInput2"
									placeholder="Enter Product Price"
									value={price}
									onChange={handlePriceChange}
								></input>
							</div>
						) : (
							<p>
								Oh oh, Not connected to IPFS. Checkout out the logs for errors
							</p>
						)}
						<div class="mb-3">
							<label for="formFileMultiple" class="form-label">
								Add Images related to Product
							</label>
							<input
								class="form-control"
								type="file"
								id="formFileMultiple"
								multiple
								onChange={handleFileChange}
							/>
						</div>
						{file && (
							<div>
								<img
									width="200"
									height="200"
									src={`https://ipfs.infura.io/ipfs/${file}`}
									alt="images"
								/>
							</div>
						)}
					</div>
					<div className="modal-footer">
						<button
							type="button"
							class="btn btn-secondary"
							data-bs-dismiss="modal"
							onClick={resetInputs}
						>
							Close
						</button>
						<button
							type="button"
							class="btn btn-primary"
							onClick={handleAddProduct}
						>
							{loading ? "Loading...." : "Add Product"}
						</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default ListProduct;
